
END_OF_WORD = 100

class Solution:
    def build_tree(self, words):
        self.head = {}
        for word in words:
            node = self.head
            for l in word:
                if l not in node:
                    node[l] = {}
                node = node[l]
            if END_OF_WORD in node:
                node[END_OF_WORD] += 1
            else:
                node[END_OF_WORD] = 1
            
    def mark_tree_nodes(self, s, pos, tree):
        if len(tree) == 0:
            return
        
        while pos < len(s):
            if s[pos] in tree:
                subtree = tree[s[pos]]
                del tree[s[pos]]

                if END_OF_WORD in subtree:
                    self.res += subtree[END_OF_WORD]
                    del subtree[END_OF_WORD]

                self.mark_tree_nodes(s, pos + 1, subtree)
                self.mark_tree_nodes(s, pos + 1, tree)
                return
            pos += 1
    
    def numMatchingSubseq(self, s: str, words: [str]) -> int:
        self.res = 0
        self.build_tree(words)

        self.mark_tree_nodes(s, 0, self.head)
        return self.res

